import React, { useState } from "react";
import { StyleSheet, Text, View, Button, TextInput } from "react-native";
import Navigator from "../routes/homestack";

export default function Splash({ navigation }) {
  const pressHandler1 = () => {
    navigation.navigate("Login");
  };

  const pressHandler2 = () => {
    navigation.navigate("Create");
  };

  const pressHandler3 = () => {
    navigation.navigate("Forgot");
  };

  return (
    <View style={styles.container}>
      <View style = {styles.header}>
        <Text style = {{textAlign: 'center', fontSize: "50%"}}>Bris-Tool</Text>
        <Text>Your Personal Health Journal</Text>
      </View>
      <View style={styles.loginBox}>
        <Button  title="Login here" onPress={pressHandler1} />

        <Button
          title="New user? Create an account here!"
          onPress={pressHandler2}
        />

        <Button
          title="Forgot Password"
          onPress={() => navigation.push("Forgot")}
        />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "lightblue",
    alignItems: "center",
    justifyContent: 'flex-start',
  },
  loginBox: {
    justifyContent: 'center',

    marginTop: "50%",
    padding: 0,
  },
  headerContainer: {
    marginTop: 10,
  }
});
